def solution(n):
    largest = 0
    index   = [0,0]
    for idx in range(len(n)-1):
        _sum = sum(n[idx:])
        for jidx in range(0, len(n)-1-idx):
            # an equal amount of 0's and 1's is the same as len/2
            if _sum == (len(n)-idx-jidx) / 2:
                if len(n)-idx-jidx > largest:
                    largest = len(n)-idx-jidx
                    index = [idx,len(n) - 1 - jidx]
                break
            else:
                _sum -= n[len(n)-1-jidx]
        if largest > len(n)-idx:
            # early exit
            break
    return (largest,index)

